import React from 'react';
import styled from 'styled-components';
import Logo from '../assets/logo.png';
import avatar from '../assets/Avatar.png';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <>
      <Container>
        <div className="foravt">
          <div className="home-page">
            <header>
              <h1>
                Welcome to <br />
                <img className="img" src={Logo} alt="logo" />
                ChatApp!
              </h1>
              <p className="p1">Join the conversation with your friends</p>
              <p className="p2">Chat with your friends from wherever you are</p>
            </header>
            <main>
              <section className="login-form">
                <button type="submit">
                  <Link to="/Login">Login to chat</Link>
                </button>
              </section>
            </main>
          </div>
          <div className="avatar">
            <img className="avata" src={avatar} alt="" />
          </div>
        </div>
        <footer>
          <p>&copy; {new Date().getFullYear()} ChatApp. All rights reserved.</p>
        </footer>
      </Container>
    </>
  );
};

export default Home;

const Container = styled.div`
  /* Your existing CSS styles */

  /* ... */

  @media screen and (max-width: 768px) {
    /* Adjust styles for tablets and smaller devices */

    .foravt {
      flex-direction: column;
      align-items: center;
    }

    .avata {
      margin-left: 0; /* Remove the left margin on smaller devices */
      margin-top: 1rem; /* Add some top margin */
      height: auto; /* Adjust the height to fit the content */
    }

    .home-page {
      margin-top: 2rem; /* Reduce the top margin on smaller devices */
    }

    h1 {
      font-size: 2.5rem; /* Reduce font size for h1 on smaller devices */
    }

    .p1,
    .p2 {
      font-size: 1.4rem; /* Reduce font size for paragraphs on smaller devices */
    }

    main {
      max-width: 90%; /* Adjust the max-width for the main section */
    }

    .login-form {
      width: 100%; /* Make the login form full width on smaller devices */
    }
  }

  @media screen and (max-width: 600px) {
    /* Adjust styles for mobile devices */

    .home-page {
      margin-top: 1rem; /* Reduce the top margin even more for smaller devices */
    }

    h1 {
      font-size: 2rem; /* Further reduce font size for h1 on smaller devices */
    }

    .p1,
    .p2 {
      font-size: 1.2rem; /* Further reduce font size for paragraphs on smaller devices */
    }
  }
`;
