import React from 'react'
import styled from "styled-components";
import Logo from "../assets/logo.png";
import { Link } from 'react-router-dom';


const Home = () => {
  return (
    <>
        <Container>
        <img src={Logo} alt="logo" />
            
        <div className="home-page">
            <header>
                <h1>Welcome to <br /> ChatApp!</h1>
                <p className='p1'>Join the conversation with your friends</p>
                <p className='p2'>Chat white your friends from wherever you are</p>
            </header>
            <main>
                <section className="login-form">
                <button type="submit"><Link to="/Login">Login to chat</Link></button>
                
                </section>
            </main>
        </div>
            <footer>
                <p>&copy; {new Date().getFullYear()} ChatApp. All rights reserved.</p>
            </footer>
            

        </Container>
    </>
  )
}

export default Home;

const Container = styled.div`
button{
    padding:10px;
}
background: linear-gradient(to bottom, #003399 38%, #000066 100%);

img{
    height:3rem;
    margin:1rem
}

.home-page {
  display: flex;
  flex-direction: column;
  align-items: start;
  margin-top: 5rem;
  text-align:justify;
  margin-left:5rem;
  height: 100vh;
}

header {
  text-align: justify;
  margin-bottom: 2rem;
  width:350px;
}

main {
  max-width: 400px;
  width: 90%;
  padding: 1rem;
  border-radius: 8px;
}

.login-form {
  margin-bottom: 1rem;
  text-align:center;
  width:100px
}

h1 {
  font-size: 3.5rem;
  font-weight:400;
}
.p1,.p2{
    font-size: 1.8rem;
    margin:1.2rem 0;
}

h2 {
  font-size: 1.8rem;
  margin-bottom: 1rem;
}
button{
    border-radius:5px;
    background:#442;
    font-size:1rem;
    color:white;

}
a {
    color: #000;
    text-decoration: none;
    font-weight: bold;
  }


footer {
  position: absolute;
  bottom: 0;
  width: 100%;
  text-align: center;
  padding: 0.5rem 0;
  background:#00004c
//   border-top: 1px solid #ccc;
}

@media screen and (max-width: 600px) {
  h1 {
    font-size: 2rem;
  }

  h2 {
    font-size: 1.5rem;
  }

  p {
    font-size: 1rem;
  }
}


`;
